mapper.select:
insert into lectures(LECTURE_NUM, LECTURE_TITLE, TEACHER_NAME, SEMESTER, SORTATION, LECTURE_ROOM, LECTURE_TIME, ENROLMENT, RECEPTION_STATUS, CAPACITY, MIDEX, FINALEX, SUBJECTCREDIT, STATE, BOOK, LECTURE_YEAR, LECTURE_DAY, HOST)
			values(#{lec_seq.nextval}, #{LECTURE_TITLE}, #{TEACHER_NAME}, #{SEMESTER}, #{SORTATION}, #{LECTURE_ROOM}
	, #{LECTURE_TIME}, #{ENROLMENT}, #{RECEPTION_STATUS, jdbcType=VARCHAR}, #{CAPACITY}
	, #{MIDEX, jdbcType=VARCHAR}, #{FINALEX, jdbcType=VARCHAR}, #{SUBJECTCREDIT}, #{STATE},#{BOOK},#{LECTURE_YEAR},#{LECTURE_DAY},#{HOST})
